plugins {
    // Apply the org.jetbrains.kotlin.jvm Plugin to add support for Kotlin.
    kotlin("jvm") version "1.6.10"

    id("com.github.johnrengelman.shadow") version "7.1.2"

    // Apply the application plugin to add support for building a CLI application in Java.
    application
}

repositories {
    // Use Maven Central for resolving dependencies.
    mavenCentral()
}

tasks.withType<org.jetbrains.kotlin.gradle.tasks.KotlinCompile> {
    kotlinOptions {
        jvmTarget = "17"
//        --add-modules jdk.incubator.foreign
        freeCompilerArgs = listOf("-Xadd-modules=jdk.incubator.foreign")
        languageVersion = "1.6"
        verbose = true
    }

}



dependencies {

    implementation(platform("net.openhft:chronicle-bom:2.22ea8"))

    implementation("net.openhft:affinity")

    // https://mvnrepository.com/artifact/net.openhft/chronicle-bytes
    implementation("net.openhft:chronicle-bytes")

    implementation("net.java.dev.jna:jna-platform:5.11.0")

    implementation("org.slf4j:slf4j-simple:1.7.36")

    // This dependency is used by the application.
//    implementation("com.google.guava:guava:30.1-jre")

}

application {
    // Define the main class for the application.
    mainClass.set("kotlin_servers.AppKt")
    applicationDefaultJvmArgs = listOf("--add-modules=jdk.incubator.foreign")

}
